INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (245219, 'Benzhydrocodone', 'Scopolamine', 'Moderate', 'Coadministration of opioids with anticholinergic agents may result in additive central nervous system (CNS), gastrointestinal, and genitourinary effects.', 'DDInter', 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.  Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289095/', '', 'Nitrazepam, Zopiclone, Dichloralphenazone, Ketorolac, Homatropine, Ephedrine, Tropicamide, Phenylephrine, Rolapitant', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (245220, 'Benzhydrocodone', 'Secobarbital', 'Major', 'Barbiturates may potentiate the central nervous system (CNS) depressant effects of opioids.  Concomitant use may result in profound sedation, respiratory depression, coma, and death.  On the other hand, some barbiturates can also induce the hepatic metabolism of opioids that are metabolized by CYP450 3A4 such as butorphanol, fentanyl, hydrocodone, methadone and oxycodone, resulting in enhanced clearance.  Reduced analgesic efficacy or withdrawal symptoms may occur in patients maintained on their opioid regimen following the addition of a barbiturate.  Conversely, discontinuation of the barbiturate may increase plasma concentrations of the opioid and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 'The use of opioids in conjunction with other CNS depressants such as barbiturates should generally be avoided unless alternative treatment options are inadequate.  If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, and patients should be closely monitored for signs and symptoms of CNS and respiratory depression.  Particular caution is advisable when a barbiturate is added to or withdrawn from therapy in patients receiving opioids that are CYP450 3A4 substrates, as there may be an increased risk of withdrawal symptoms (e.g., restlessness, insomnia, sweating, lacrimation, or rhinorrhea) following initiation of the barbiturate and overdose following discontinuation.  A dosage adjustment for one or both drugs may be required.', 'Metabolism, Synergism', 'The use of opioids in conjunction with other CNS depressants such as barbiturates should generally be avoided unless alternative treatment options are inadequate.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289096/', '', 'Nitrazepam, Zopiclone, Dichloralphenazone', '', 1767369485);
