INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (245646, 'Upadacitinib', 'Tramadol', 'Moderate', 'Coadministration of Janus kinase (JAK) inhibitors with corticosteroids, nonsteroidal anti-inflammatory drugs (NSAIDs), and/or opioids may increase the risk of gastrointestinal (GI) perforation.', 'DDInter', 'Caution is recommended when using JAK inhibitors in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as corticosteroids, NSAIDs, and opioids.  Patients should be advised to contact their healthcare provider if they experience signs and symptoms of GI perforation such as severe abdominal pain, fever, chills, nausea, or vomiting.', 'Synergism', 'Caution is recommended when using JAK inhibitors in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as corticosteroids, NSAIDs, and opioids.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289559/', '', 'Oliceridine, Naltrexone, Oxymorphone, Tapentadol, Butorphanol, Acetaminophen, Hydromorphone, Dezocine, Fentanyl, Methadone, Naloxone, Nalbuphine', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
