INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (245969, 'Dihydrocodeine', 'Pentazocine', 'Major', 'Concomitant use of opioids with other central nervous system (CNS) depressants including mixed agonist-antagonist or partial agonist opioids may result in profound sedation, respiratory depression, coma, and death.  The risk of hypotension may also be increased.  On the other hand, mixed agonist-antagonist or partial agonist opioids can reduce the pharmacologic effects of other opioid agonists.', 'DDInter', 'The use of opioids in conjunction with other CNS depressants including mixed agonist-antagonist or partial agonist opioids should generally be avoided unless alternative treatment options are inadequate.  If coadministration is necessary (e.g., when initiating a switch from one opioid to the other), the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, and patients should be closely monitored for signs and symptoms of CNS and respiratory depression.  Additional caution is advisable when a mixed agonist-antagonist or partial agonist opioid is added to an existing opioid regimen, as there may be an increased risk of withdrawal symptoms (e.g., restlessness, insomnia, sweating, lacrimation, or rhinorrhea) following initiation of the mixed agonist-antagonist or partial agonist opioid.  A dosage adjustment for one or both drugs may be required.', 'Others', 'The use of opioids in conjunction with other CNS depressants including mixed agonist-antagonist or partial agonist opioids should generally be avoided unless alternative treatment options are inadequate.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289910/', '', 'Dihydrocodeine, Acetylsalicylic acid, Acetaminophen, Ibuprofen', 'Pentazocine, Acetylsalicylic acid, Acetaminophen, Ibuprofen', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (245970, 'Dihydrocodeine', 'Pentobarbital', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death.  The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', 'DDInter', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.  If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed.  Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them.  Cough medications containing opioids (e.g., codeine, hydrocodone) should not be prescribed to patients using benzodiazepines or other CNS depressants including alcohol.  For patients who have been receiving extended therapy with both an opioid and a benzodiazepine and require discontinuation of either medication, a gradual tapering of dose is advised, since abrupt withdrawal may lead to withdrawal symptoms.  Severe cases of benzodiazepine withdrawal, primarily in patients who have received excessive doses over a prolonged period, may result in numbness and tingling of extremities, hypersensitivity to light and noise, hallucinations, and epileptic seizures.', 'Synergism', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289911/', '', 'Nitrazepam, Zopiclone, Dichloralphenazone', 'Naltrexone, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 1767369485);
