INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246221, 'Erlotinib', 'Budesonide', 'Moderate', 'Coadministration of erlotinib with anti-angiogenic agents, corticosteroids, nonsteroidal anti-inflammatory drugs (NSAIDs), and/or taxane-based chemotherapy may increase the risk of gastrointestinal (GI) perforation.', 'DDInter', 'Caution is recommended when using erlotinib in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as anti-angiogenic agents, corticosteroids, NSAIDs, and taxane-based chemotherapy.  Patients should be advised to contact their healthcare provider if they experience signs and symptoms of GI perforation such as severe abdominal pain, fever, chills, nausea, or vomiting.  The manufacturer recommends that erlotinib should be permanently discontinued in patients who develop gastrointestinal perforation.', 'Synergism', 'Caution is recommended when using erlotinib in patients with a history of peptic ulceration or diverticular disease and in patients receiving concomitant treatment with drugs associated with an increased risk of GI perforation such as anti-angiogenic agents, corticosteroids, NSAIDs, and taxane-based chemotherapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290469/', '', 'Formoterol, Cromoglicic acid, Ipratropium, Isoprenaline, Beclomethasone dipropionate, Terbutaline, Aclidinium, Glycopyrronium, Mometasone, Orciprenaline, Pirbuterol, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 1767369485);
