INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246224, 'Budesonide', 'Somapacitan', 'Moderate', 'Human growth hormone may reduce the pharmacologic effects of corticosteroids.  Growth hormone is thought to enhance the activity of CYP450 3A4, an enzyme involved in the catabolism of corticosteroids.  In addition, growth hormone (GH) inhibits microsomal enzyme 11-beta-hydroxysteroid dehydrogenase type 1 (11BHSD-1), which is required for conversion of cortisone to its active metabolite, cortisol, in hepatic and adipose tissue.  Consequently, individuals with untreated GH deficiency have relative increases in 11BHSD-1 and serum cortisol, and initiation of GH therapy may result in inhibition of 11BHSD-1 and reduced serum cortisol levels.', 'DDInter', 'Higher dosages of corticosteroids may be required during concomitant use of human growth hormone.  Patients treated with glucocorticoid replacement for previously diagnosed hypoadrenalism may require an increase in their maintenance or stress doses following initiation of GH treatment.  In addition, glucocorticoid replacement dosing should be carefully adjusted in children receiving concomitant GH treatment to avoid both hypoadrenalism and an inhibitory effect on growth.', 'Antagonism', 'Higher dosages of corticosteroids may be required during concomitant use of human growth hormone.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290475/', '', 'Mometasone furoate, Terbutaline, Umeclidinium, Aclidinium, Glycopyrronium, Epinephrine, Olodaterol, Cromoglicic acid, Ipratropium, Salbutamol, Orciprenaline, More', 'Corticotropin, Mecasermin, Sermorelin, Tesamorelin, Thyrotropin alfa, Tetracosactide, Mecasermin rinfabate, Pegvisomant', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246225, 'Abametapir (topical)', 'Netupitant', 'Moderate', 'Based on in vitro inhibition data, a single topical application of abametapir lotion may increase plasma concentrations of drugs that are substrates of CYP450 3A4, CYP450 2B6, and CYP450 1A2 isoenzymes and increase their systemic concentrations.', 'DDInter', 'Use of CYP450 3A4, CYP450 2B6, and CYP450 1A2 substrates should generally be avoided within 2 weeks after topical application of abametapir lotion.  If this is not feasible, the manufacturer recommends avoiding use of abametapir lotion.  If abametapir is used, monitor for increased toxicity of the CYP450 3A4, CYP450 2B6, and/or CYP450 1A2 substrate.', 'Metabolism', 'Use of CYP450 3A4, CYP450 2B6, and CYP450 1A2 substrates should generally be avoided within 2 weeks after topical application of abametapir lotion.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290477/', '', '', '', 1767369485);
