INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246440, 'Pimozide', 'Netupitant', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of pimozide, which is partially metabolized by the isoenzyme.', 'DDInter', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors is considered contraindicated.  Some authorities consider concomitant administration of pimozide and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.  With respect to less potent CYP450 3A4 inhibitors, the manufacturers recommend that they also not be used with pimozide.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290728/', '', 'Fluphenazine, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Thioridazine, Trifluoperazine, Paliperidone, Chlorpromazine, Mesoridazine, More', '', 1767369485);
