INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246558, 'Fosnetupitant', 'Bexarotene', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may theoretically increase the plasma concentrations of bexarotene.  In vitro studies suggest that bexarotene is metabolized by CYP450 3A4.  However, concomitant administration with multiple doses of ketoconazole, a potent CYP450 3A4 inhibitor, did not alter bexarotene plasma concentrations, which would imply that bexarotene elimination is not substantially dependent on CYP450 3A4 metabolism in vivo.  Conversely, bexarotene appears to be an inducer of CYP450 3A4 and may reduce the plasma concentrations of CYP450 3A4 inhibitors that are also substrates of the isoenzyme, such as azole antifungals, macrolides, some calcium channel blockers, mifepristone, and nefazodone.', 'DDInter', 'We continue to conservatively recommend avoiding concomitant use of drugs that are CYP450 3A4 inhibitors such as azole antifungals, macrolides, some calcium channel blockers, mifepristone, and nefazodone.', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290867/', '', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246559, 'Fosnetupitant', 'Binimetinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp), uridine diphosphate glucuronosyltransferase (UGT) 1A1 or 2B7, and/or breast cancer resistance protein (BCRP) may increase the plasma levels and risk of adverse effects of binimetinib.  The proposed mechanism involves the reduced metabolic clearance of binimetinib through inhibition of P-gp, UGT 1A1, UGT 2B7, and/or BCRP.', 'DDInter', 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors.  Binimetinib should be monitored more closely whenever a P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitor is added to or withdrawn from therapy, and the binimetinib dosage adjusted as necessary.', 'Metabolism', 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290868/', '', 'Sunitinib, Futibatinib, Regorafenib, Vemurafenib, Lenvatinib, Dabrafenib, Erdafitinib, Vandetanib, Trametinib, Nintedanib, Ibrutinib, More', '', 1767369485);
