INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246574, 'Fosnetupitant', 'Capmatinib', 'Moderate', 'Coadministration of capmatinib with moderate CYP450 3A4 inhibitors may increase the risk and severity of capmatinib adverse effects, such as interstitial lung disease, pneumonitis, and hepatotoxicity.  The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4, which is one of the primary enzymes responsible for the metabolic clearance of capmatinib.', 'DDInter', 'Close monitoring is recommended whenever capmatinib is used with a moderate CYP450 3A4 inhibitor.  Clinical and laboratory monitoring should be considered whenever a moderate CYP450 3A4 inhibitor is added to or withdrawn from therapy with capmatinib, and the dosage adjusted as necessary based on clinical response and toxicity.  Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Close monitoring is recommended whenever capmatinib is used with a moderate CYP450 3A4 inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290884/', '', 'Sunitinib, Futibatinib, Regorafenib, Vemurafenib, Lenvatinib, Dabrafenib, Erdafitinib, Vandetanib, Trametinib, Nintedanib, Ibrutinib, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246575, 'Fosnetupitant', 'Cariprazine', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of cariprazine and its major active metabolite, didesmethyl cariprazine (DDCAR), both of which are primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised when cariprazine is prescribed with moderate CYP450 3A4 inhibitors.  Patients should be monitored for adverse effects such as extrapyramidal symptoms, cognitive and motor impairment, hyperglycemia, dyslipidemia, weight gain, orthostatic hypotension, leukopenia, neutropenia, seizures and dysphagia, and the dosage of cariprazine adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when cariprazine is prescribed with moderate CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290886/', '', 'Fluphenazine, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Thioridazine, Trifluoperazine, Paliperidone, Chlorpromazine, Mesoridazine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246576, 'Fosnetupitant', 'Ceritinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ceritinib, which is a substrate of the isoenzyme.', 'DDInter', 'Caution is advised if ceritinib is prescribed with CYP450 3A4 inhibitors.  Pharmacologic response to ceritinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the ceritinib dosage adjusted as necessary.  Patients should have periodic ECGs and be monitored for arrhythmias when QT interval is prolonged.  A QTc interval exceeding 500 msec on at least two separate ECGs will require suspension of ceritinib therapy and immediate action to correct any concomitant risk factors before resuming treatment with a 150 mg dosage reduction.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.  Following discontinuation of the CYP450 3A4 inhibitor, ceritinib therapy should be resumed at the dosage that was taken prior to initiating the CYP450 3A4 inhibitor if an adjustment was made.', 'Metabolism', 'Caution is advised if ceritinib is prescribed with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290887/', '', 'Sunitinib, Futibatinib, Regorafenib, Vemurafenib, Lenvatinib, Dabrafenib, Erdafitinib, Vandetanib, Trametinib, Nintedanib, Ibrutinib, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246577, 'Fosnetupitant', 'Cerivastatin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of HMG-CoA reductase inhibitors (i.e., statins) that are metabolized by the isoenzyme.  Lovastatin and simvastatin are particularly susceptible because of their low oral bioavailability, but others such as atorvastatin and cerivastatin may also be affected.  High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity.  Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally.  Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.  Clinically significant interactions have been reported with potent CYP450 3A4 inhibitors such as macrolide antibiotics, azole antifungals, protease inhibitors and nefazodone, and moderate inhibitors such as amiodarone, cyclosporine, danazol, diltiazem and verapamil.', 'DDInter', 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor.  It is advisable to monitor lipid levels and use the lowest effective statin dose.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.  Fluvastatin, pravastatin, and rosuvastatin are not expected to interact with CYP450 3A4 inhibitors.', 'Metabolism', 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290888/', '', 'Cholestyramine, Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Probucol, Ezetimibe, Niacin, Colesevelam, Fluvastatin, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246578, 'Fosnetupitant', 'Cevimeline', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of cevimeline, which is metabolized by these isoenzymes.', 'DDInter', 'Dosage adjustments and clinical monitoring may be appropriate whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy.  Patients should be advised to notify their physician if they experience excessive adverse cholinergic effects of cevimeline such as nausea, vomiting, diarrhea, sweating, salivation, urinary frequency, visual disturbance, confusion, tremor, palpitations, or irregular heartbeat.', 'Metabolism', 'Dosage adjustments and clinical monitoring may be appropriate whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290889/', '', 'Bethanechol, Neostigmine, Pyridostigmine, Ambenonium, Pilocarpine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246579, 'Fosnetupitant', 'Chloroquine', 'Moderate', 'Coadministration with moderate or strong inhibitors of CYP450 2C8 or CYP450 3A4 may increase the plasma concentration of hydroxychloroquine and the risk of toxicities such as QT interval prolongation and ventricular arrhythmias.  Following coadministration with cimetidine, a weak to moderate CYP450 3A4 inhibitor, a 2-fold increase in chloroquine exposure occurred.  Because chloroquine and hydroxychloroquine have similar structures and metabolic elimination pathways, a similar interaction could be observed for hydroxychloroquine.', 'DDInter', 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor.  Clinical and laboratory monitoring should be considered whenever a moderate or strong CYP450 3A4 or 2C8 inhibitor is added to or withdrawn from therapy with hydroxychloroquine or chloroquine, and the dosage adjusted as necessary.  Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290890/', '', 'Artesunate, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Primaquine, Proguanil, Sulfadoxine', '', 1767369485);
