INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246593, 'Fosnetupitant', 'Crizotinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of crizotinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if crizotinib is prescribed in combination with moderate CYP450 3A4 inhibitors.  Regular monitoring of ECG and serum electrolytes, including potassium, magnesium and calcium, may be appropriate in some patients.  Treatment should be interrupted in patients who develop Grade 3 QTc prolongation until recovery to less than or equal to Grade 1, then resumed at 200 mg twice daily.  In case of recurrence of Grade 3 QTc prolongation, therapy should be withheld until recovery to less than or equal to Grade 1, then resumed at 250 mg once daily.  Permanently discontinue crizotinib therapy if Grade 3 QTc prolongation recurs or at any time during treatment if Grade 4 QTc prolongation develops.  Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Caution is advised if crizotinib is prescribed in combination with moderate CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290906/', '', 'Sunitinib, Futibatinib, Regorafenib, Vemurafenib, Lenvatinib, Dabrafenib, Erdafitinib, Vandetanib, Trametinib, Nintedanib, Ibrutinib, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246594, 'Fosnetupitant', 'Cyclophosphamide', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by netupitant and its desmethyl metabolite.', 'DDInter', 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy.  The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Metabolism', 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290907/', '', 'Temozolomide, Thiotepa, Carmustine, Busulfan, Streptozocin, Chlorambucil, Lomustine, Dacarbazine, Mechlorethamine, Bendamustine, Melphalan, More', '', 1767369485);
