INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246620, 'Fosnetupitant', 'Eletriptan', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eletriptan, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, nefazodone, delavirdine, most protease inhibitors, and ketolide and certain macrolide antibiotics.  The manufacturer makes no specific recommendations for use with less potent inhibitors, but caution is appropriate.  Patients should have vital signs monitored regularly and advised to notify their physician if they experience signs and symptoms of vasospasm such as numbness, tingling, or cyanosis in the extremities; muscle pains; weakness; or chest pain or tightness.  Alternatively, other 5-HT1 receptor agonists that are not metabolized by CYP450 3A4 may be considered, such as frovatriptan, naratriptan, rizatriptan, sumatriptan, and zolmitriptan.', 'Metabolism', 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, nefazodone, delavirdine, most protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290936/', '', 'Frovatriptan, Eptinezumab, Lasmiditan, Rizatriptan, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, Erenumab, Clonidine, Almotriptan, Fremanezumab', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246621, 'Fosnetupitant', 'Eliglustat', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4.', 'DDInter', 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs).  In the absence of a concomitant CYP450 2D6 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to EMs treated with a moderate CYP450 3A4 inhibitor.  However, eliglustat should not be used with a moderate CYP450 3A4 inhibitor in CYP450 2D6 IMs or poor metabolizers (PMs).  Moderate CYP450 3A4 inhibitors include aprepitant, ciprofloxacin, clotrimazole, crizotinib, darunavir, diltiazem, dronedarone, fluconazole, fusidic acid, imatinib, isavuconazonium, miconazole, mifepristone, netupitant, quinupristin-dalfopristin, ranolazine, stiripentol, and verapamil.  Potent and moderate CYP450 2D6 inhibitors include abiraterone, bupropion, celecoxib, cimetidine, cinacalcet, clobazam, darifenacin, diphenhydramine, duloxetine, fluoxetine, methotrimeprazine, mirabegron, paroxetine, propoxyphene, quinidine, ranolazine, sertraline, stiripentol, and terbinafine.', 'Metabolism', 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs).', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290937/', '', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246622, 'Fosnetupitant', 'Encorafenib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of encorafenib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided.  If coadministration is required, the manufacturer recommends reducing the encorafenib dose to one-half of the dose used prior to addition of the moderate CYP450 3A4 inhibitor.  After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the encorafenib dose that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290938/', '', 'Sunitinib, Futibatinib, Regorafenib, Vemurafenib, Lenvatinib, Dabrafenib, Erdafitinib, Vandetanib, Trametinib, Nintedanib, Ibrutinib, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246623, 'Fosnetupitant', 'Enfortumab vedotin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of enfortumab vedotin.', 'DDInter', 'Caution is advised when enfortumab vedotin is used concomitantly with CYP450 3A4 inhibitors.  Patients should be closely monitored for development or exacerbation of toxicities such as peripheral neuropathy, hyperglycaemia, dermatologic reactions (maculopapular rash, pruritus, symmetrical drug-related intertriginous and flexural exanthema (SDRIFE), bullous dermatitis, exfoliative dermatitis, palmar-plantar erythrodysesthesia), and ocular disorders (dry eyes, keratitis, blurred vision, limbal stem cell deficiency), and the dosing of enfortumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when enfortumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290939/', '', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (246624, 'Fosnetupitant', 'Entrectinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of entrectinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided when possible.  If coadministration is necessary, the dosage of entrectinib should be reduced to 200 mg once daily for adults and pediatrics patients (12 years and older) with a body surface area (BSA) greater than 1.5 m2.  Following discontinuation, and after an appropriate washout period of the CYP450 3A4 inhibitor (i.e., 3 to 5 half-lives), the entrectinib dosage taken prior to initiating the CYP450 3A4 inhibitor may be resumed.  For pediatric patients with a body surface area of 1.5 m2 or less, concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided.  Some authorities recommend if coadministration is unavoidable, adult patients should follow dose reduction guidance as described above while limiting coadministration to 14 days and do not recommend coadministration in pediatric patients (UK).', 'Metabolism', 'Concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided when possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290940/', '', 'Sunitinib, Futibatinib, Regorafenib, Vemurafenib, Lenvatinib, Dabrafenib, Erdafitinib, Vandetanib, Trametinib, Nintedanib, Ibrutinib, More', '', 1767369485);
