INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (247586, 'Cyclosporine', 'Norgestimate', 'Moderate', 'Coadministration with some estrogens or progestins may increase the plasma concentrations of cyclosporine.  The risk of cyclosporine-induced nephrotoxicity, hepatotoxicity, and neurotoxicity may be increased.  The proposed mechanism is competitive inhibition of cyclosporine metabolism via CYP450 3A4, of which estrogens and progestins are also substrates.', 'DDInter', 'Caution is advised during concomitant therapy with cyclosporine.  Cyclosporine blood levels as well as renal and hepatic function should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of hormonal contraceptives in patients who are stabilized on their cyclosporine regimen.  Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity (e.g., fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, jaundice) or neurotoxicity (e.g., tremor, convulsions).', 'Metabolism', 'Caution is advised during concomitant therapy with cyclosporine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/292715/', '', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Methotrexate, Abatacept, Upadacitinib, Siponimod, Apremilast, Inebilizumab, Belumosudil, More', 'Estetrol', 1767369485);
