INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (247863, 'Desogestrel', 'Lamotrigine', 'Moderate', 'Coadministration with estrogens or progestins may decrease the plasma concentrations and pharmacologic effects of lamotrigine due to induction of lamotrigine glucuronidation.', 'DDInter', 'Pharmacologic response and plasma lamotrigine levels should be monitored more closely whenever estrogen- and/or progestin-containing drugs are added to or withdrawn from therapy, and the lamotrigine dosage adjusted as necessary.  The manufacturer''s labeling should be consulted for detailed dosage recommendations.  Patients should be advised to contact their physician if they experience loss of seizure control or symptoms of lamotrigine toxicity such as ataxia, nystagmus, increased seizures, irregular heartbeat, and changes in mental status.  In patients receiving oral contraceptives, gradual transient increases in lamotrigine levels will occur during the pill-free week for women not also taking an enzyme-inducing drug (e.g., carbamazepine, phenytoin, phenobarbital, primidone, rifampin).  The increase in lamotrigine levels will be greater if the dose of lamotrigine is increased in the few days before or during the pill-free week.  Although diminished contraceptive efficacy has not been reported, the possibility should be considered.  Patients should be instructed to promptly report changes in their menstrual pattern.', 'Metabolism', 'Pharmacologic response and plasma lamotrigine levels should be monitored more closely whenever estrogen- and/or progestin-containing drugs are added to or withdrawn from therapy, and the lamotrigine dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293017/', '', 'Ezogabine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, Ganaxolone, Vigabatrin, Mephenytoin, More', 'Megestrol acetate, Ulipristal, Dienogest, Megestrol acetate, Dienogest', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (247864, 'Desogestrel', 'Lapatinib', 'Moderate', 'Coadministration with lapatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter.  The mechanism is decreased clearance via these routes due to inhibition by lapatinib.', 'DDInter', 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lapatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293018/', '', 'Imatinib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Ribociclib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, More', 'Estetrol', 1767369485);
