INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (247999, 'Bosutinib', 'Lumefantrine', 'Major', 'Artemether-lumefantrine may cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of artemether-lumefantrine with other drugs that can prolong the QT interval should generally be avoided.  Caution is recommended when these drugs are used following treatment with artemether-lumefantrine due to the long elimination half-life of lumefantrine (3 to 6 days).  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of artemether-lumefantrine with other drugs that can prolong the QT interval should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293175/', '', 'Imatinib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, More', 'Artesunate, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Proguanil, Sulfadoxine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (248000, 'Brigatinib', 'Artemether', 'Moderate', 'Coadministration with brigatinib may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  Brigatinib has been shown in vitro to induce CYP450 3A4 at clinically relevant plasma concentrations.', 'DDInter', 'Caution is advised when brigatinib is used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever brigatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when brigatinib is used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293176/', '', 'Entrectinib, Imatinib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Lapatinib, Selumetinib, Vandetanib, Cobimetinib, More', 'Lumefantrine, Pyrimethamine, Tafenoquine, Atovaquone, Hydroxychloroquine, Primaquine, Proguanil, Sulfadoxine, Chloroquine', 1767369485);
