INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (248338, 'Diclofenac', 'Tafasitamab', 'Moderate', 'Coadministration of tafasitamab with drugs that can affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, dextrans, and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk of bleeding.', 'DDInter', 'When possible, consideration should be given to withholding concomitant medications that may increase bleeding risk in patients treated with tafasitamab.  Close clinical and laboratory monitoring for bleeding complications is recommended if concomitant use is unavoidable.  Refer to the product labeling for guidance on frequency of laboratory monitoring and dose modifications or withholding recommendations for platelet counts below 50,000/mcL.  Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools.', 'Synergism', 'When possible, consideration should be given to withholding concomitant medications that may increase bleeding risk in patients treated with tafasitamab.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293552/', '', 'Fludrocortisone, Indomethacin, Fluocinolone acetonide, Hydrocortisone, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Salicylic acid, Ketorolac, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Amivantamab, Atezolizumab, Pembrolizumab, More', 1767369485);
