INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (248860, 'Canagliflozin', 'Azilsartan medoxomil', 'Moderate', 'Sodium-glucose co-transporter 2 (SGLT-2) inhibitors may potentiate the hypotensive effects of diuretics and other antihypertensive agents or vasodilators.  Inhibition of glucose and sodium co-transport produces mild diuresis and transient natriuresis, resulting in intravascular volume contraction.', 'DDInter', 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function.  Prior to initiating SGLT-2 inhibitors, volume status should be assessed and corrected, if necessary.  Clinical and laboratory monitoring are recommended during therapy, including electrolytes, fluid status, renal function, and blood pressure.  If volume depletion occurs, treatment with SGLT-2 inhibitors should be interrupted until the condition is corrected.', 'Synergism', 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294140/', '', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Dulaglutide, Tolbutamide, Miglitol, Glimepiride, More', 'Sacubitril', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (248861, 'Captopril', 'Azilsartan medoxomil', 'Major', 'Coadministration of an ACE inhibitor in combination with an angiotensin II receptor antagonist may increase the risk of hyperkalemia, hypotension, syncope, and renal dysfunction due to additive or synergistic effects on the renin-angiotensin system.', 'DDInter', 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy.  Most patients receiving the combination do not obtain any additional benefit compared to monotherapy.  However, if the combination is considered medically necessary, serum electrolytes, blood pressure, and renal function should be closely monitored.  Routine monitoring of electrolytes and renal function may be indicated in the elderly or patients with worsening heart failure or a risk for dehydration.  Potassium supplementation should generally be avoided unless it is closely monitored, and patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294141/', '', 'Felodipine, Amlodipine, Verapamil, Bisoprolol, Hydrochlorothiazide, Indapamide', 'Nebivolol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (248862, 'Cariprazine', 'Azilsartan medoxomil', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity.', 'DDInter', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.  A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.  Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294142/', '', 'Droperidol, Pimozide, Pimavanserin', 'Sacubitril, Aliskiren', 1767369485);
