INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249018, 'Riociguat', 'Azilsartan medoxomil', 'Moderate', 'Riociguat may potentiate the action of antihypertensive agents and other vasodilators.  Symptomatic hypotension may occur.  Riociguat stimulates soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cyclic guanosine monophosphate (cGMP).  The resultant accumulation of cGMP in vascular smooth muscle cells leads to peripheral vasodilation and blood pressure decreases.', 'DDInter', 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.  A reduction in the starting dosage of riociguat to 0.5 mg three times a day should be considered for patients who may not tolerate the hypotensive effect of riociguat.  Periodic systemic blood pressure monitoring is recommended.  If systolic blood pressure remains greater than 95 mmHg and the patient has no signs or symptoms of hypotension, the dosage may be increased by 0.5 mg three times a day every 2 weeks as tolerated, up to a maximum of 2.5 mg three times a day.  If hypotension develops at any time during treatment, the dosage should be reduced by 0.5 mg three times a day.', 'Synergism', 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294302/', '', 'Bosentan, Ambrisentan, Macitentan, Metyrosine', 'Sacubitril', 1767369485);
