INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249025, 'Selexipag', 'Azilsartan medoxomil', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents.', 'DDInter', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.  If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.  Treatment guidelines should be consulted for the optimum management of pulmonary arterial hypertension.', 'Synergism', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294309/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 'Sacubitril', 1767369485);
