INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249049, 'Triamterene', 'Azilsartan medoxomil', 'Major', 'Concomitant use of angiotensin II receptor blockers (ARBs) and potassium-sparing diuretics may increase the risk of hyperkalemia.  Inhibition of angiotensin II results in decreased aldosterone secretion, which can lead to increases in serum potassium that may be additive with that induced by potassium-sparing diuretics.', 'DDInter', 'Caution is advised if angiotensin II receptor blockers must be used concurrently with potassium-sparing diuretics, particularly in patients with renal impairment, diabetes, old age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium such as nonsteroidal anti-inflammatory drugs, beta-blockers, cyclosporine, heparin, tacrolimus, and trimethoprim.  Serum potassium and renal function should be checked prior to initiating therapy and regularly thereafter, and potassium supplementation as well as the use of potassium-containing salt substitutes should be avoided unless absolutely necessary and the benefits outweigh the potential risks.  Patients should be given counseling on the appropriate levels of potassium and fluid intake, and advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.  If spironolactone is prescribed with an ARB, some investigators recommend that its dosage not exceed 25 mg/day in high-risk patients.', 'Synergism', 'Caution is advised if angiotensin II receptor blockers must be used concurrently with potassium-sparing diuretics, particularly in patients with renal impairment, diabetes, old age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium such as nonsteroidal anti-inflammatory drugs, beta-blockers, cyclosporine, heparin, tacrolimus, and trimethoprim.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294337/', '', 'Amlodipine, Sacubitril, Hydrochlorothiazide', '', 1767369485);
