INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249488, 'Dihydrotachysterol', 'Bendroflumethiazide', 'Moderate', 'Coadministration of thiazide diuretics with high dosages of calcium and/or vitamin D has been associated with reports of hypercalcemia in some patients.  Thiazide diuretics inhibit the renal excretion of calcium and may also enhance responsiveness of bone and renal tubule to parathyroid hormone, thus concurrent use of large amounts of calcium or vitamin D can lead to excessively high plasma levels of calcium.', 'DDInter', 'Patients receiving thiazide diuretic therapy should be cautioned against self-treatment with calcium and vitamin D supplements without first talking to their healthcare provider.  Serum calcium should be monitored if thiazide diuretics are coadministered with high dosages of calcium and/or vitamin D.  Patients should be advised to seek medical attention if they experience signs and symptoms of hypercalcemia such as dizziness, weakness, lethargy, headache, myalgia, anorexia, nausea, vomiting, and seizures.', 'Excretion', 'Patients receiving thiazide diuretic therapy should be cautioned against self-treatment with calcium and vitamin D supplements without first talking to their healthcare provider.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295294/', '', 'Trichlormethiazide, Furosemide, Trichlormethiazide, Bumetanide, Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, More', 'Vitamin A, Beta carotene', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249489, 'Dimethyl fumarate', 'Bendroflumethiazide', 'Moderate', 'The use of fumaric acid esters in combination with medications that can cause nephrotoxicity (e.g., aminoglycosides, diuretics, nonsteroidal anti-inflammatory drugs, cyclosporine, lithium, methotrexate) may increase the potential for adverse renal reactions.', 'DDInter', 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.  Assessment of renal function (e.g., serum creatinine, blood urea nitrogen, urinalysis) is recommended prior to initiating treatment with fumaric acid esters and as clinically indicated during treatment.', 'Synergism', 'The use of fumaric acid esters in patients who receive concomitant treatment with potentially nephrotoxic agents, particularly for longer durations, has not been evaluated and should be approached with caution.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295295/', '', 'Polythiazide, Hydroflumethiazide, Chlorothiazide, Trichlormethiazide, Methyclothiazide, Furosemide, Trichlormethiazide, Bumetanide, Metolazone, Tamsulosin, Tinidazole, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Tacrolimus, Abatacept, More', 1767369485);
