INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249589, 'Enzalutamide', 'Bictegravir', 'Major', 'Coadministration with potent inducers of CYP450 3A4, particularly those that can also induce uridine diphosphate glucuronosyltransferase (UGT) 1A1, may significantly decrease the plasma concentrations of bictegravir.  According to the product labeling, bictegravir is a substrate of both CYP450 3A4 and UGT1A1; however, the extent to which each enzymatic pathway contributes to the metabolic clearance of bictegravir has not been reported.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of bictegravir with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295434/', '', 'Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, Nilutamide, Exemestane', 'Lamivudine, Cabotegravir, Zanamivir, Valaciclovir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, Voxilaprevir, More', 1767369485);
