INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249775, 'Tetracycline', 'Ketoconazole', 'Major', 'Use of hepatotoxic drugs together with ketoconazole or levoketoconazole may potentiate the risk of liver injury.', 'DDInter', 'The use of ketoconazole or levoketoconazole with other potentially hepatotoxic agents should be avoided.  Patients treated with levoketoconazole or ketoconazole should have liver tests performed prior to and during treatment.  Levoketoconazole manufacturer recommends interrupting treatment immediately if signs of hepatotoxicity occur.  Refer to levoketoconazole labeling for specific instructions on management of hepatotoxicity.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'The use of ketoconazole or levoketoconazole with other potentially hepatotoxic agents should be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295896/', '', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Mitapivat, Amphotericin B, Ciclopirox, Vemurafenib, Darunavir, Probenecid, Tioconazole, More', 'Rifamycin, Polymyxin B, Chloramphenicol, Gentamicin, Chlorhexidine, Neomycin, Tinidazole, Misoprostol, Bismuth subcitrate potassium, Amoxicillin, Epinephrine, More', 1767369485);
