INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249801, 'Atorvastatin', 'Norelgestromin', 'Minor', 'Coadministration with atorvastatin may increase the plasma concentrations of estrogens and progestins.  The exact mechanism of interaction is unknown but may be related to competitive inhibition of CYP450 3A4, the isoenzyme responsible for the metabolism of atorvastatin, estrogens, some (if not all) progestins, and other steroids.', 'DDInter', 'The clinical significance of these alterations is unknown but should be considered when selecting oral contraceptive and hormone replacement doses.', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295934/', '', 'Amlodipine, Ramipril, Valsartan, Simvastatin, Lovastatin, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, More', 'Megestrol acetate, Ulipristal, Estetrol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249802, 'Berotralstat', 'Norelgestromin', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of CYP450 3A4 and/or 2D6.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 and 2D6 activity by berotralstat.', 'DDInter', 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.  Clinical and laboratory monitoring are recommended following the initiation of berotralstat, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295937/', '', 'Hemin, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 'Estetrol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249803, 'Bexarotene', 'Norelgestromin', 'Major', 'There are no data concerning the potential effect of bexarotene on hormonal contraceptives.  Presumably, coadministration with bexarotene may decrease the efficacy of contraceptives containing low-dose estrogens and progestins due to its induction of CYP450 3A4, the isoenzyme that is primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with bexarotene.  Because use of bexarotene is likely associated with major birth defects, it is particularly important that patients not become pregnant during treatment.  Therefore, hormonal contraceptives should not be used as the sole method of birth control in women of childbearing potential treated with bexarotene.  Product labeling recommends that two reliable forms of contraception, one non-hormonal, be used simultaneously during bexarotene therapy and for at least one month following discontinuation of therapy.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with bexarotene.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295938/', '', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Porfimer sodium, More', 'Estetrol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249804, 'Boceprevir', 'Norelgestromin', 'Major', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may decrease the plasma concentrations and efficacy of hormonal contraceptives.  The mechanism involves induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 'Systemic hormonal contraceptives may be continued, but may not be as effective, during and for up to two weeks following treatment with boceprevir or telaprevir.  Because HCV NS3/4A protease inhibitors must be used in combination with peginterferon alfa and ribavirin, and use of ribavirin has been associated with significant birth defects and fetal death, it is particularly important that patients not become pregnant during treatment.  Therefore, systemic hormonal contraceptives should not be relied upon as an effective method of birth control in women of childbearing potential treated with HCV NS3/4A protease inhibitors.  Two alternative, effective methods of contraception, which may include intrauterine devices and barrier methods, should be used during HCV NS3/4A protease inhibitor treatment in combination with peginterferon alfa and ribavirin.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Two weeks after completion of HCV NS3/4A protease inhibitor treatment, hormonal contraceptives are again appropriate as one of the two required effective methods of birth control for patients receiving peginterferon alfa and ribavirin.', 'Metabolism', 'Systemic hormonal contraceptives may be continued, but may not be as effective, during and for up to two weeks following treatment with boceprevir or telaprevir.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295939/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Ulipristal, Estetrol', 1767369485);
