INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249887, 'Norelgestromin', 'Nilotinib', 'Moderate', 'Based on in vitro inhibition data, coadministration with nilotinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter and/or CYP450 2D6, CYP450 3A4, or UGT1A1 isoenzymes.  The mechanism is decreased clearance via these routes due to inhibition by nilotinib.', 'DDInter', 'Caution is advised when nilotinib is used concurrently with drugs that are known P-gp and/or CYP450 2D6, CYP450 3A4 or UGT1A1 substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever nilotinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when nilotinib is used concurrently with drugs that are known P-gp and/or CYP450 2D6, CYP450 3A4 or UGT1A1 substrates, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296029/', '', 'Imatinib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Ribociclib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, More', 'Dienogest, Estetrol, Etynodiol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249888, 'Olaparib', 'Norelgestromin', 'Moderate', 'Coadministration with olaparib may decrease the plasma concentrations and efficacy of contraceptive hormones.  The proposed mechanism is olaparib induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 'Some authorities recommend that women avoid pregnancy during olaparib treatment due to its potential teratogenic effects.  In women of childbearing potential, alternative or additional non-hormonal methods of birth control should be considered during treatment and for one month after receiving the last dose of olaparib.  Regular pregnancy tests are also recommended.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Some authorities recommend that women avoid pregnancy during olaparib treatment due to its potential teratogenic effects.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296030/', '', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Porfimer sodium, More', 'Ulipristal, Megestrol acetate, Estradiol, Medroxyprogesterone acetate, Estetrol', 1767369485);
