INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (249937, 'Ulipristal', 'Norelgestromin', 'Moderate', 'Coadministration of ulipristal with combination hormonal contraceptives may reduce the efficacy of both treatments for the prevention of pregnancy.  Ulipristal and the progestin component of hormonal contraceptives both bind to the progesterone receptor.  Theoretically, competitive inhibition of the receptor may occur, which may diminish the effects of each on ovulation.', 'DDInter', 'Concomitant use of ulipristal with combination hormonal contraceptives should be avoided.  After emergency contraception with ulipristal, routine contraception should be continued or initiated as soon as possible to ensure ongoing prevention of pregnancy.  Patients who wish to use progestogen-containing contraceptives should do so no sooner than 5 days after taking ulipristal, and they should use a reliable barrier method of contraception until their next menstrual period.', 'Antagonism', 'Concomitant use of ulipristal with combination hormonal contraceptives should be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296085/', '', 'Norelgestromin, Norethisterone, Etonogestrel, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Estradiol, Levonorgestrel, Medroxyprogesterone acetate, Desogestrel, More', 'Ulipristal, Megestrol acetate, Estradiol, Estetrol, Ethinylestradiol', 1767369485);
