INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (250024, 'Deserpidine', 'Levonordefrin', 'Moderate', 'Postganglionic adrenergic blocking agents may potentiate the pharmacologic effects of direct-acting sympathomimetic amines, while the hypotensive effect of the former may be diminished.  Postganglionic adrenergic blocking agents such as guanadrel, guanethidine, and rauwolfia alkaloids work by depleting catecholamine stores from adrenergic nerve endings, thus they may sensitize adrenergic receptors to direct-acting sympathomimetics.', 'DDInter', 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.  Conversely, alternatives to postganglionic adrenergic blocking agents should be considered in patients treated with sympathomimetic amines.  If the combination is used, blood pressure and heart rate should be closely monitored.', 'Antagonism', 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296205/', '', 'Prazosin, Clonidine, Hydralazine, Guanfacine, Clonidine, Methyldopa', '', 1767369485);
