INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (250064, 'Methylergometrine', 'Levonordefrin', 'Major', 'Additive or synergistic increases in blood pressure and/or ischemic response may occur when ergot alkaloids are combined with peripheral or central vasoconstrictors.  Ergot alkaloids produce arterial vasoconstriction by stimulating alpha-adrenergic and serotonin receptors and inhibiting endothelial-derived relaxation factor release.', 'DDInter', 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents.  If concomitant use is required, patients should be monitored for excessive vasoconstriction and have vital signs measured regularly.  Patients should also be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296249/', '', 'Misoprostol, Dinoprostone', '', 1767369485);
