INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (250156, 'Halazepam', 'Oxtriphylline', 'Minor', 'Methylxanthines may antagonize the sedative effects of benzodiazepines.  Aminophylline in particular has been demonstrated to attenuate the sedative effects of diazepam.  The mechanism of this interaction may be related to competitive binding to intracerebral adenosine receptors.', 'DDInter', 'Methylxanthines may antagonize the sedative effects of benzodiazepines.  Aminophylline in particular has been demonstrated to attenuate the sedative effects of diazepam.  The mechanism of this interaction may be related to competitive binding to intracerebral adenosine receptors.  One study demonstrated that alprazolam serum concentrations were significantly lower in patients receiving chronic theophylline therapy than in patients not receiving theophylline.  Clinical and laboratory monitoring is warranted if these agents are given concurrently.  Patients receiving this combination should be monitored for adequate response to the benzodiazepine.', 'Antagonism', 'Methylxanthines may antagonize the sedative effects of benzodiazepines.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296353/', '', 'Meprobamate, Buspirone, Hydroxyzine', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Montelukast, Mepolizumab, Roflumilast, Pentoxifylline, Zafirlukast, Bromotheophylline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (250157, 'Interferon alfa-n1', 'Oxtriphylline', 'Moderate', 'The coadministration with interferons has been associated with decreases in clearance and increases in plasma concentration and elimination half-life of theophylline.  The mechanism is unknown but may be related to interferon-induced suppression of hepatic CYP450 enzymes, particularly, CYP450 1A2.  Physiologic events and drugs that induce interferon synthesis (e.g., viral infection, influenza vaccination) have also been reported to decrease metabolism and potentiate the effects of certain drugs including theophylline.', 'DDInter', 'Caution is advised if theophylline or other methylxanthines are administered with an interferon.  Pharmacologic response and serum theophylline levels should be monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of interferon therapy in patients who are stabilized on their methylxanthine regimen.  Patients should be advised to contact their physician if they experience signs and symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat.', 'Metabolism', 'Caution is advised if theophylline or other methylxanthines are administered with an interferon.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296355/', '', 'Oprelvekin, Sipuleucel-T, Elapegademase, Sargramostim, Histamine, Aldesleukin, Pegademase, Pegfilgrastim, Filgrastim, Glatiramer', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Mepolizumab, Pentoxifylline, Bromotheophylline', 1767369485);
