INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (250195, 'Ampicillin', 'Picosulfuric acid', 'Moderate', 'Theoretically, use of sodium picosulfate during or following antibiotic treatment may result in a reduced laxative effect of sodium picosulfate.  The proposed mechanism involves antibiotic-induced reduction of colonic bacteria that hydrolyze sodium picosulfate, a prodrug, to its active metabolite bis-(p-hydroxyphenyl)-pyridyl-2-methane (BHPM), a compound that stimulates colonic peristalsis.', 'DDInter', 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.  If concomitant use is required, additional bowel cleansing and colonoscopy may be required.', 'Antagonism', 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296403/', '', 'Tazobactam, Sulbactam, Ganciclovir, Acyclovir, Famciclovir, Trifluridine, Chlorhexidine, Povidone-iodine', 'Alvimopan, Sorbitol, Tegaserod, Naloxegol, Bisacodyl, Naldemedine, Methylnaltrexone, Lactitol, Sodium phosphate, monobasic, Magnesium oxide, Sodium sulfate, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (250196, 'Anagrelide', 'Picosulfuric acid', 'Moderate', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval.', 'DDInter', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.  Monitoring of baseline and posttreatment serum electrolyte levels is recommended, particularly in the elderly.  Patients should be instructed to drink plenty of clear liquids before, during, and after the bowel preparation process.  Consideration should be given to consumption of 36 to 48 fluid ounces of a carbohydrate-electrolyte solution in the six hours before the first dose.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296404/', '', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Alvimopan, Sorbitol, Magnesium sulfate, Methylnaltrexone, Tegaserod, Magnesium oxide, Naloxegol, Plecanatide, Lubiprostone, Methylcellulose, Mannitol, More', 1767369485);
