INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (250421, 'Picosulfuric acid', 'Linaclotide', 'Minor', 'The concomitant use of linaclotide with proton pump inhibitors, laxatives, or nonsteroidal anti-inflammatory drugs may increase the risk of diarrhea.', 'DDInter', 'Concurrent use of linaclotide with proton pump inhibitors, laxatives, or nonsteroidal anti-inflammatory drugs requires attention to whether the patient develops severe diarrhea.', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296640/', '', 'Alvimopan, Sorbitol, Tegaserod, Naloxegol, Bisacodyl, Naldemedine, Methylnaltrexone, Lactitol, Sodium phosphate, monobasic, Magnesium oxide, Prucalopride, More', 'Alvimopan, Sodium sulfate, Sorbitol, Methylnaltrexone, Tegaserod, Magnesium oxide, Naloxegol, Plecanatide, Lactitol, Lubiprostone, Mannitol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (250422, 'Picosulfuric acid', 'Lincomycin', 'Moderate', 'Theoretically, use of sodium picosulfate during or following antibiotic treatment may result in a reduced laxative effect of sodium picosulfate.  The proposed mechanism involves antibiotic-induced reduction of colonic bacteria that hydrolyze sodium picosulfate, a prodrug, to its active metabolite bis-(p-hydroxyphenyl)-pyridyl-2-methane (BHPM), a compound that stimulates colonic peristalsis.', 'DDInter', 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.  If concomitant use is required, additional bowel cleansing and colonoscopy may be required.', 'Antagonism', 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296641/', '', 'Alvimopan, Sorbitol, Tegaserod, Naloxegol, Bisacodyl, Naldemedine, Methylnaltrexone, Lactitol, Sodium phosphate, monobasic, Magnesium oxide, Sodium sulfate, More', '', 1767369485);
