INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251116, 'Dalfopristin', 'Cannabidiol', 'Minor', 'Cannabidiol is a substrate for CYP450 3A4 and CYP450 2C19.  Cannabidiol is metabolized in the liver and the gut (primarily in the liver) by CYP450 2C19 and CYP450 3A4 enzymes, and UGT1A7, UGT1A9, and UGT2B7 isoforms.', 'DDInter', 'Cannabidiol is a substrate for CYP450 3A4 and CYP450 2C19.  Cannabidiol is metabolized in the liver and the gut (primarily in the liver) by CYP450 2C19 and CYP450 3A4 enzymes, and UGT1A7, UGT1A9, and UGT2B7 isoforms.  Coadministration of cannabidiol with itraconazole (a potent inhibitor of CYP450 3A4) increased cannabidiol exposure by less than 10%; exposure to fluconazole (a potent CYP450 2C19 inhibitor) was increased to less than 20%.  Both values are not considered clinically meaningful.', 'Metabolism', 'Cannabidiol is a substrate for CYP450 3A4 and CYP450 2C19.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297438/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Phenytoin, Lacosamide, Levetiracetam, Oxcarbazepine, Ethotoin, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251117, 'Dalfopristin', 'Capmatinib', 'Moderate', 'Coadministration of capmatinib with moderate CYP450 3A4 inhibitors may increase the risk and severity of capmatinib adverse effects, such as interstitial lung disease, pneumonitis, and hepatotoxicity.  The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4, which is one of the primary enzymes responsible for the metabolic clearance of capmatinib.', 'DDInter', 'Close monitoring is recommended whenever capmatinib is used with a moderate CYP450 3A4 inhibitor.  Clinical and laboratory monitoring should be considered whenever a moderate CYP450 3A4 inhibitor is added to or withdrawn from therapy with capmatinib, and the dosage adjusted as necessary based on clinical response and toxicity.  Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Close monitoring is recommended whenever capmatinib is used with a moderate CYP450 3A4 inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297440/', '', 'Dabrafenib, Dacomitinib, Vemurafenib, Vandetanib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, Erlotinib, More', '', 1767369485);
