INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251298, 'Dalfopristin', 'Retapamulin (topical)', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of retapamulin, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of retapamulin, which is primarily metabolized by the isoenzyme.  In healthy adult males, coadministration of the potent CYP450 3A4 inhibitor ketoconazole (200 mg orally twice a day) increased the peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of retapamulin by 81% following topical application of retapamulin ointment 1% on abraded skin.  However, dosage adjustments are not necessary due to the low systemic exposure to retapamulin following topical application.', 'Metabolism', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of retapamulin, which is primarily metabolized by the isoenzyme.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297641/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251299, 'Dalfopristin', 'Ribociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and risk of adverse effects of ribociclib, which is a substrate of the isoenzyme.', 'DDInter', 'Caution is advised when ribociclib is used with CYP450 3A4 inhibitors.  Dosage adjustment for ribociclib is not generally considered necessary for suspected interactions with mild to moderate inhibitors.  However, some manufacturers of ribociclib recommend that concomitant use with moderate CYP450 3A4 inhibitor verapamil should be avoided (AU, UK).  Patients should be monitored for the development of ribociclib-related adverse effects such as infections, neutropenia, leucopenia, anemia, thrombocytopenia, nausea, vomiting, diarrhea, stomatitis, anorexia, alopecia, fatigue, headache, and abnormal liver function.', 'Metabolism', 'Caution is advised when ribociclib is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297642/', '', 'Dabrafenib, Dacomitinib, Vemurafenib, Vandetanib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, Erlotinib, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251300, 'Dalfopristin', 'Rilpivirine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of rilpivirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution and clinical monitoring may be advisable if CYP450 3A4 inhibitors are coadministered with rilpivirine.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297643/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, More', '', 1767369485);
