INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251360, 'Dalfopristin', 'Zileuton', 'Moderate', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dalfopristin-quinupristin.', 'DDInter', 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dalfopristin-quinupristin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297711/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251361, 'Dalfopristin', 'Ziprasidone', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may only modestly increase the plasma concentrations of ziprasidone, as less than 1/3 of ziprasidone metabolic clearance occurs via oxidation mediated by CYP450 3A4.', 'DDInter', 'Coadministration with inhibitors of CYP450 3A4 may only modestly increase the plasma concentrations of ziprasidone, as less than 1/3 of ziprasidone metabolic clearance occurs via oxidation mediated by CYP450 3A4.  In 14 healthy subjects, coadministration with the potent CYP450 3A4 inhibitor ketoconazole (400 mg orally once a day for 5 days) increased the mean peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of ziprasidone (40 mg single oral dose) by approximately 1/3 compared to placebo.  These changes, although statistically significant, were not considered clinically important.  There were also no serious adverse events, laboratory or ECG abnormalities, or clinically significant alterations in vital signs throughout the study.  Likewise, in 10 healthy subjects, the nonspecific CYP450 inhibitor cimetidine (800 mg orally once a day for 3 days) increased the AUC of ziprasidone (40 mg single oral dose) by just 6% compared to when ziprasidone was administered alone.  These findings suggest that ziprasidone dose modifications are unlikely to be necessary in patients receiving potent CYP450 3A4 inhibitors.', 'Metabolism', 'Coadministration with inhibitors of CYP450 3A4 may only modestly increase the plasma concentrations of ziprasidone, as less than 1/3 of ziprasidone metabolic clearance occurs via oxidation mediated by CYP450 3A4.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297712/', '', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Thioridazine, Trifluoperazine, Paliperidone, Chlorpromazine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251362, 'Dalfopristin', 'Zolpidem', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of zolpidem, which is partially metabolized by the isoenzyme.', 'DDInter', 'Caution is advised when zolpidem is used with CYP450 3A4 inhibitors.  Patients should be advised to avoid driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination until they know how the medications affect them, and to notify their physician if they experience excessive somnolence or dizziness.', 'Metabolism', 'Caution is advised when zolpidem is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297713/', '', 'Methohexital, Methylphenobarbital, Pentobarbital, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Butabarbital, Secobarbital, Chloral hydrate, Quazepam, More', '', 1767369485);
