INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251456, 'Esomeprazole', 'Paritaprevir', 'Moderate', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may decrease the plasma concentrations of omeprazole.  The proposed mechanism of interaction is CYP450 2C19 induction by ritonavir.', 'DDInter', 'Patients should be monitored for potentially diminished efficacy of omeprazole during coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir.  The dosage of omeprazole may be increased in patients whose symptoms are not well controlled; however, some authorities suggest that the dosage of omeprazole should be limited to 40 mg/day.', 'Metabolism', 'Patients should be monitored for potentially diminished efficacy of omeprazole during coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297855/', '', 'Tinidazole, Pantoprazole, Levofloxacin, Dexlansoprazole, Rabeprazole, Sucralfate, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, More', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Telaprevir, Dolutegravir, Oseltamivir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251457, 'Esterified estrogens', 'Paritaprevir', 'Major', 'Theoretical concerns exist that coadministration of estrogen-containing products may increase the risk of liver enzyme elevations associated with the use of ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.  The proposed mechanism is the inhibition of UDP-glucuronosyltransferase (UGT) by ombitasvir, paritaprevir, and dasabuvir.', 'DDInter', 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.  Liver function tests should be performed on all patients during the first 4 weeks of treatment and as clinically indicated thereafter.  If ALT is elevated above baseline at any time during treatment, the test should be repeated and monitored closely.  Therapy should be discontinued if ALT levels remain persistently greater than 10 times the ULN, or if ALT elevations are accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Metabolism', 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297856/', '', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Telaprevir, Velpatasvir, More', '', 17