INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251659, 'Brentuximab vedotin', 'Quinidine', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of monomethyl auristatin E (MMAE), the microtubule-disrupting component of brentuximab vedotin.  In vitro, MMAE was found to be a substrate of the P-gp efflux transporter.', 'DDInter', 'Caution is advised when brentuximab is used with P-gp inhibitors.  Close monitoring for adverse effects including neutropenia, infection, peripheral neuropathy, and hepatotoxicity is recommended.', 'Excretion', 'Caution is advised when brentuximab is used with P-gp inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298375/', '', 'Lidocaine, Tocainide, Propafenone, Procainamide, Flecainide, Disopyramide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251660, 'Glycopyrronium (topical)', 'Quinidine', 'Moderate', 'The potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (onset or exacerbation) when topical or inhaled anticholinergic agents are used with each other or with other agents that possess anticholinergic properties.  The risk of systemic anticholinergic effects following topical administration depends on variables such as strength of the product, size of the application area, frequency of application, and use of occlusive dressing.  Systemic effects are uncommon following oral inhalation or nasal administration due to the poor absorption of quaternary ammonium compounds from gastrointestinal and nasal mucosa.', 'DDInter', 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).  Caution is advised if concomitant use cannot be avoided, particularly in the elderly and those with significantly impaired renal and/or hepatic function.  Measures should be taken whenever possible to minimize ocular exposure to these drugs, such as keeping eyes closed during oral inhalation, use of a mouthpiece rather than face mask during nebulization, and not touching the eyes following topical application until hands are washed with soap and water.  Patients should be advised to contact their physician if they experience excessive anticholinergic adverse effects or signs and symptoms of angle-closure glaucoma (e.g., eye pain or discomfort; blurred vision; visual halos; colored images in association with red eyes from conjunctival congestion or corneal edema).', 'Synergism', 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298376/', '', 'Amiodarone, Dronedarone, Tocainide, Lidocaine, Propafenone, Flecainide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', '', 1767369485);
