INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251670, 'Aminophylline', 'Dienogest', 'Moderate', 'The coadministration with contraceptive steroids may increase the plasma concentrations of theophylline and other methylxanthines.  The proposed mechanism is inhibition of methylxanthine metabolism via hepatic microsomal enzymes, of which estrogens and progestins are also substrates.', 'DDInter', 'Caution is advised during concomitant therapy with methylxanthines and hormonal contraceptives.  Serum theophylline levels and pharmacologic response should be monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of hormonal contraceptives in patients who are stabilized on their theophylline regimen.  Patients should be advised to notify their physician if they experience signs and symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, and irregular heartbeat.', 'Metabolism', 'Caution is advised during concomitant therapy with methylxanthines and hormonal contraceptives.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298390/', '', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Mepolizumab, Roflumilast, Pentoxifylline, More', 'Megestrol acetate, Ulipristal, Estetrol, Medroxyprogesterone acetate, Megestrol acetate, Progesterone, Medroxyprogesterone acetate, Megestrol acetate, Progesterone, Medroxyprogesterone acetate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251671, 'Amobarbital', 'Dienogest', 'Moderate', 'Coadministration with a barbiturate may reduce the efficacy of contraceptive hormones.The interaction stems from accelerated clearance of contraceptive hormones as well as decreased plasma concentrations of unbound (active) hormones due to induction of hepatic CYP450 enzymatic activity and enhancement of hormone-binding globulin capacity by phenobarbital.  Since all barbiturates are believed to possess enzyme-inducing capabilities, the interaction should be expected with agents in the class other than phenobarbital.', 'DDInter', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with barbiturates.  Alternative or additional methods of birth control should be used during and for at least one week after short-term and 4 weeks after long-term (greater than 4 weeks) barbiturate therapy.  No precautions or recommendations are available for women using hormone-releasing intrauterine systems, but a significant interaction with these systems is thought to be unlikely due to their local action.  Injectable progestin-only contraceptives are also thought to be unaffected by barbiturates.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with barbiturates.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298391/', '', 'Suvorexant, Triazolam, Flurazepam, Methohexital, Zolpidem, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Chloral hydrate, Quazepam, More', 'Estetrol', 1767369485);
