INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251766, 'Dienogest', 'Lomitapide', 'Moderate', 'Coadministration with weak inhibitors of CYP450 3A4 including oral contraceptives may increase the plasma concentrations of lomitapide, which is primarily metabolized by the isoenzyme.', 'DDInter', 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors including oral contraceptives.  Likewise, if oral contraceptives are initiated during treatment with lomitapide, the dosage of lomitapide may require adjustment.  Patients should be advised that hormone absorption from oral contraceptives may be incomplete if vomiting or diarrhea occurs while taking lomitapide, which would warrant the use of additional contraceptive methods, especially since lomitapide may be teratogenic.', 'Metabolism', 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors including oral contraceptives.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298489/', '', 'Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Ezetimibe, Niacin, Fluvastatin, More', 'Ulipristal, Norelgestromin, Megestrol acetate, Estradiol, Medroxyprogesterone acetate, Estetrol, Etonogestrel, Megestrol acetate, Progesterone, Medroxyprogesterone acetate, Megestrol acetate, More', 1767369485);
