INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251799, 'Dienogest', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may render hormonal contraceptives ineffective.  The mechanism for the potential interaction has not been established and there are no clinical data regarding the use of pralsetinib with hormonal contraceptives.', 'DDInter', 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 2 weeks after the final dose.  In addition, men receiving pralsetinib should use effective contraception during treatment and for 1 week after the final dose.', 'Others', 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 2 weeks after the final dose.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298525/', '', 'Imatinib, Asciminib, Acalabrutinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Ribociclib, More', 'Megestrol acetate, Estetrol, Megestrol acetate, Megestrol acetate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251800, 'Dienogest', 'Pramlintide', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, sirolimus, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents.  These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 'Caution is advised when drugs that can interfere with glucose metabolism are prescribed to patients with diabetes.  Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.  Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.  Likewise, patients should be observed for hypoglycemia when these drugs are withdrawn from their therapeutic regimen.', 'Antagonism', 'Caution is advised when drugs that can interfere with glucose metabolism are prescribed to patients with diabetes.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298526/', '', 'Simvastatin, Guar gum, Rosuvastatin', 'Ulipristal, Norelgestromin', 1767369485);
