INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251952, 'Difenoxin', 'Tizanidine', 'Moderate', 'The sedative effect of tizanidine may be potentiated by concomitant use of other agents with central nervous system (CNS) depressant effects.  In addition, tizanidine and many of these agents (e.g., alcohol, anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, muscle relaxants) also can exhibit hypotensive effects, which may be additive during coadministration and may increase the risk of symptomatic hypotension and orthostasis, particularly during initiation of therapy or dose escalation.', 'DDInter', 'Caution is advised and more gradual dose titration may be appropriate when tizanidine is used with other substances that can cause CNS depression and/or hypotension, particularly in elderly or debilitated patients.  Ambulatory patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to avoid rising abruptly from a sitting or recumbent position.  Patients should contact their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or excessive CNS effects that interfere with their normal activities.', 'Synergism', 'Caution is advised and more gradual dose titration may be appropriate when tizanidine is used with other substances that can cause CNS depression and/or hypotension, particularly in elderly or debilitated patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298687/', '', 'Orphenadrine, Chlorzoxazone, Cyclobenzaprine, Methocarbamol, Chlormezanone', 'Eluxadoline', 1767369485);
