INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252403, 'Velpatasvir', 'Apalutamide', 'Major', 'Coadministration with potent or moderate inducers of CYP450 isoenzymes may decrease the plasma concentrations of velpatasvir, which has been shown in vitro to be metabolized by CYP450 2B6, 2C8, and 3A4.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of sofosbuvir-velpatasvir with potent or moderate CYP450 inducers is not recommended.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299423/', '', 'Darolutamide, Tamoxifen, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Bicalutamide, Degarelix, Nilutamide, More', 'Lamivudine, Cabotegravir, Zanamivir, Valaciclovir, Telaprevir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252404, 'Velpatasvir', 'Apixaban', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of apixaban, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 'No dosage adjustment for apixaban is required during concomitant therapy with lone inhibitors of CYP450 3A4 or P-gp, or moderate or weak dual inhibitors of both CYP450 3A4 and P-gp.  However, caution may be advisable.  Closer monitoring of the pharmacologic effects of apixaban may be appropriate whenever a CYP450 3A4 or P-gp inhibitor is added to or withdrawn from therapy.  Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women).', 'Metabolism', 'No dosage adjustment for apixaban is required during concomitant therapy with lone inhibitors of CYP450 3A4 or P-gp, or moderate or weak dual inhibitors of both CYP450 3A4 and P-gp.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299424/', '', 'Ticagrelor, Clopidogrel, Cilostazol, Iloprost, Rivaroxaban, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252405, 'Velpatasvir', 'Atorvastatin', 'Moderate', 'Coadministration with velpatasvir may increase the plasma concentrations of the HMG-CoA reductase inhibitors atorvastatin and simvastatin.  The proposed mechanism has not been delineated but may involve velpatasvir inhibition of the hepatic uptake transporter organic anion transporting polypeptide 1B (OATP1B), and the efflux transporters P-glycoprotein (P-gp), and/or breast cancer resistance protein (BCRP), of which atorvastatin and simvastatin are substrates.', 'DDInter', 'The lowest dose of atorvastatin or simvastatin should be used when prescribed with sofosbuvir-velpatasvir, and the dosage titrated cautiously.  Pravastatin may be considered as a substitute.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by fever, malaise, and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism, Distribution', 'The lowest dose of atorvastatin or simvastatin should be used when prescribed with sofosbuvir-velpatasvir, and the dosage titrated cautiously.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299425/', '', 'Amlodipine, Ramipril, Valsartan, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, More', 'Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, Doravirine, Bictegravir, Pibrentasvir, More', 1767369485);
