INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252601, 'Glecaprevir', 'Digoxin', 'Moderate', 'Coadministration with glecaprevir-pibrentasvir may increase the serum concentrations of digoxin.  The proposed mechanism is inhibition of the P-glycoprotein (P-gp)-mediated intestinal efflux or renal tubular secretion of digoxin by glecaprevir and pibrentasvir, both of which are inhibitors of the transporter.', 'DDInter', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of glecaprevir-pibrentasvir.  The manufacturer recommends measuring serum digoxin levels before initiating glecaprevir-pibrentasvir and reducing the levels by decreasing the digoxin dose approximately 50% or by modifying the dosing frequency, followed by continued monitoring.  Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of glecaprevir-pibrentasvir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299992/', '', 'Digitoxin', 'Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Remdesivir, Elbasvir, More', 1767369485);
