INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252603, 'Glecaprevir', 'Edoxaban', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of edoxaban, which is a substrate of the efflux transporter.', 'DDInter', 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil.  This dosage recommendation is based on data from a clinical study, the Hokusai VTE study, and is limited to use with the specific P-gp inhibitors mentioned.  Other P-gp inhibitors were not permitted in the study, and patients on antiretroviral therapy (ritonavir, nelfinavir, indinavir, saquinavir) as well as cyclosporine were excluded from the study.  Following discontinuation of the P-gp inhibitor, edoxaban dosage should be returned to the regular dosage of 60 mg once daily.  No dosage adjustment is recommended for edoxaban when used in the treatment of nonvalvular atrial fibrillation.', 'Absorption', 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299995/', '', 'Ticagrelor, Clopidogrel, Cilostazol, Iloprost, Rivaroxaban, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole,