INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252642, 'Itraconazole', 'Glecaprevir', 'Moderate', 'Coadministration with itraconazole may increase the plasma concentrations and risk of adverse effects of glecaprevir-pibrentasvir.  The mechanism has not been clearly established, but may involve itraconazole-mediated inhibition of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or CYP450 3A4 isoenzyme.', 'DDInter', 'Caution and monitoring for adverse effects is recommended if itraconazole is used concomitantly with glecaprevir-pibrentasvir.  A dosage adjustment should be considered whenever itraconazole is added to or withdrawn from therapy with these drugs.', 'Metabolism', 'Caution and monitoring for adverse effects is recommended if itraconazole is used concomitantly with glecaprevir-pibrentasvir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300061/', '', 'Fluconazole, Caspofungin, Micafungin, Amphotericin B, Miconazole, Posaconazole, Ketoconazole, Flucytosine, Anidulafungin, Voriconazole', 'Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Oseltamivir, Brincidofovir, Famciclovir, Voxilaprevir, Tecovirimat, Maribavir, More', 1767369485);
