INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252729, 'Aluminum hydroxide', 'Ledipasvir', 'Moderate', 'Concurrent administration of ledipasvir with drugs that elevate gastric pH such as antacids may reduce its gastrointestinal absorption.  Ledipasvir exhibits pH-dependent solubility, with increased solubility at lower pH.  Drugs that increase gastric pH are expected to decrease the plasma concentration of ledipasvir.', 'DDInter', 'If an antacid is required, the antacid should be taken 4 hours before or 4 hours after administration of ledipasvir-sofosbuvir.  The same precaution may be applicable to other drugs with antacid effects.', 'Absorption', 'If an antacid is required, the antacid should be taken 4 hours before or 4 hours after administration of ledipasvir-sofosbuvir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300200/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Etravirine, More', '', 1767369485);
