INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252731, 'Apalutamide', 'Ledipasvir', 'Moderate', 'Coadministration with apalutamide may decrease the plasma concentrations of drugs that are substrates of the metabolic enzymes CYP450 3A4, CYP450 2C19, CYP450 2C9, and uridine diphosphate glucuronosyltransferase (UGT), as well as substrates of the membrane transporters organic anion transporting polypeptide (OATP) 1B1, P-glycoprotein (P-gp), and breast cancer resistance protein (BCRP).', 'DDInter', 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, CYP450 2C19, CYP450 2C9, UGT, OATP1B1, P-gp and/or BCRP, particularly sensitive substrates or those with a narrow therapeutic range.  Substitution for these medications is recommended when possible, or monitor for potential loss of therapeutic efficacy if coadministration is required.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration of a CYP450 inducer like apalutamide and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, CYP450 2C19, CYP450 2C9, UGT, OATP1B1, P-gp and/or BCRP, particularly sensitive substrates or those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300202/', '', 'Darolutamide, Tamoxifen, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Enzalutamide, Bicalutamide, Degarelix, More', 'Lamivudine, Cabotegravir, Zanamivir, Valaciclovir, Telaprevir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, More', 1767369485);
