INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252768, 'Famotidine', 'Ledipasvir', 'Moderate', 'Coadministration of ledipasvir with H2-receptor antagonists may reduce its gastrointestinal absorption.  Ledipasvir exhibits pH-dependent solubility, with increased solubility at lower pH.', 'DDInter', 'H2-receptor antagonists may be administered simultaneously with or 12 hours apart from ledipasvir-sofosbuvir at a dose that does not exceed the equivalent of famotidine 40 mg twice daily.', 'Absorption', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300243/', '', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Rifabutin, Clarithromycin, Vonoprazan, Amoxicillin', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Etravirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252769, 'Fexofenadine', 'Ledipasvir', 'Moderate', 'Coadministration with ledipasvir may increase the plasma concentrations of orally administered drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters.  The proposed mechanism is decreased clearance in the intestine due to inhibition of P-gp or BCRP-mediated drug efflux by ledipasvir.', 'DDInter', 'Caution is advised if ledipasvir must be used concurrently with medications that are substrates of the P-gp and/or BCRP transporters, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ledipasvir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if ledipasvir must be used concurrently with medications that are substrates of the P-gp and/or BCRP transporters, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300244/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Promethazine, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Cabotegravir, Telaprevir, Velpatasvir, Rilpivirine, Etravirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252770, 'Fidaxomicin', 'Ledipasvir', 'Minor', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter.Theoretically, concentrations of fidaxomicin and OP-1118 may also be decreased at the site of action (i.e., gastrointestinal tract) due to P-gp inhibition.', 'DDInter', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter.  When fidaxomicin 200 mg was administered in combination with cyclosporine 200 mg in 14 study subjects, fidaxomicin peak plasma concentration (Cmax) and systemic exposure (AUC) increased by an average of 4.2- and 1.9-fold, respectively, compared to when administered alone.  The Cmax and AUC of OP-1118 was increased by 9.5- and 4.1-fold, respectively, with cyclosporine.  Theoretically, concentrations of fidaxomicin and OP-1118 may also be decreased at the site of action (i.e., gastrointestinal tract) due to P-gp inhibition.  However, concomitant use of a P-gp inhibitor had no attributable effect on safety or efficacy of fidaxomicin in controlled clinical trials.  No dosage adjustment is recommended when fidaxomicin is coadministered with P-gp inhibitors.', 'Absorption, Excretion', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300245/', '', 'Rifamycin, Kanamycin, Amphotericin B, Miconazole, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Paromomycin, Neomycin', 'Lamivudine, Simeprevir, Amprenavir, Cabotegravir, Zanamivir, Valaciclovir, Rilpivirine, Etravirine, Dolutegravir, Oseltamivir, Cobicistat, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252771, 'Fluvastatin', 'Ledipasvir', 'Moderate', 'Coadministration of HMG-CoA reductase inhibitors (statins) with HCV NS5A/NS5B inhibitors, such as ledipasvir, may significantly increase the plasma concentrations of the statin and the risk of adverse effects, such as myopathy and rhabdomyolysis.  The proposed mechanism of this interaction has not been clearly delineated but may involve ledipasvir-mediated inhibition of the metabolism of statins via hepatic transport enzymes and/or CYP450 isoenzymes.', 'DDInter', 'Caution and close monitoring are recommended if ledipasvir is used concomitantly with a statin and a dosage reduction for the statin drug considered.  Patients should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, fever, malaise, and/or dark colored urine, particularly during the initial months of therapy and during any periods of upward dosage titration of either drug.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Caution and close monitoring are recommended if ledipasvir is used concomitantly with a statin and a dosage reduction for the statin drug considered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300246/', '', 'Gemfibrozil, Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Cholestyramine, Ezetimibe, Clofibrate, Colestipol, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Telaprevir, Velpatasvir, Rilpivirine, More', 1767369485);
