INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (252791, 'Ledipasvir', 'Lapatinib', 'Moderate', 'Coadministration with ledipasvir may increase the plasma concentrations of orally administered drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters.  The proposed mechanism is decreased clearance in the intestine due to inhibition of P-gp or BCRP-mediated drug efflux by ledipasvir.', 'DDInter', 'Caution is advised if ledipasvir must be used concurrently with medications that are substrates of the P-gp and/or BCRP transporters, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ledipasvir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if ledipasvir must be used concurrently with medications that are substrates of the P-gp and/or BCRP transporters, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300266/', '', 'Entrectinib, Imatinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, More', 1767369485);
