INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253001, 'Homatropine', 'Physostigmine (ophthalmic)', 'Moderate', 'Theoretically, anticholinergic agents and other agents with significant anticholinergic activity (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, class IA antiarrhythmics especially disopyramide) may antagonize the effects of topically administered cholinergic agents such as acetylcholine, carbachol, demecarium, echothiophate, isoflurophate, physostigmine, and pilocarpine.', 'DDInter', 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300496/', '', 'Tropicamide, Ephedrine, Ketorolac, Phenylephrine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253002, 'Homatropine', 'Pilocarpine', 'Moderate', 'Anticholinergic agents and other agents with significant anticholinergic activity (e.g., clozapine, class IA antiarrhythmics especially disopyramide) may antagonize the effects of direct-acting cholinergic agents such as bethanechol, carbachol, cevimeline, and pilocarpine.  This interaction is sometimes desirable and is the basis for using atropine in the treatment of excessive muscarinic side effects and cholinergic crisis induced by cholinergic overdose.  Conversely, cholinergic agents may also counteract the pharmacologic effects of anticholinergic agents and other agents that rely partially on their anticholinergic activity for therapeutic effects (e.g., some antiparkinsonian and antiemetic/antivertigo agents).  The mechanism of interaction involves opposing pharmacodynamic action on muscarinic receptor sites.', 'DDInter', 'It may be appropriate to avoid concomitant use of anticholinergic agents and cholinergic agents, depending on the needs of the patient.  If concurrent use is necessary, the patient should be monitored for reduced pharmacologic effects of both drugs.', 'Antagonism', 'It may be appropriate to avoid concomitant use of anticholinergic agents and cholinergic agents, depending on the needs of the patient.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300497/', '', 'Latanoprost, Apraclonidine, Methazolamide, Clonidine, Diclofenamide, Bimatoprost, Travoprost, Acetazolamide, Brinzolamide, Epinephrine, Brimonidine, More', 'Ephedrine, Tropicamide, Phenylephrine', 1767369485);
