INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253197, 'Isometheptene', 'Bupropion', 'Moderate', 'Translated with DeepL', 'DDInter', 'Caution is advised when bupropion is used with other drugs that increase dopaminergic or noradrenergic activity due to an increased risk of hypertension.  Blood pressure and heart rate should be measured prior to initiating bupropion therapy and monitored at regular intervals consistent with usual clinical practice, particularly in patients with preexisting hypertension.  Dose reduction or discontinuation of bupropion should be considered in patients who experience clinically significant and sustained increases in blood pressure or heart rate.', 'Synergism', 'Caution is advised when bupropion is used with other drugs that increase dopaminergic or noradrenergic activity due to an increased risk of hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300739/', '', 'Dexfenfluramine, Naltrexone, Ephedrine, Fenfluramine, Orlistat, Lorcaserin, Citalopram, Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, More', 'Mepenzolate, Propantheline, Glycopyrronium, Papaverine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253198, 'Isometheptene', 'Deserpidine', 'Moderate', 'Sympathomimetic amines may decrease the hypotensive effect of postganglionic adrenergic blocking agents, while the latter may potentiate the pharmacologic effects of direct-acting sympathomimetic amines (e.g., dobutamine, epinephrine, methoxamine, norepinephrine) but inhibit those that are primarily indirect-acting (e.g., mephentermine).  Postganglionic adrenergic blocking agents such as guanadrel, guanethidine, and rauwolfia alkaloids work by depleting catecholamine stores from adrenergic nerve endings.  Therefore, they may sensitize adrenergic receptors to direct-acting sympathomimetics, but blunt the effects of indirect-acting agents whose activity is mediated through the release of catecholamines.  Guanethidine and reserpine have been reported to attenuate the pharmacologic effects (mydriasis, pressor response) induced by ephedrine and dopamine, both of which exhibit direct and indirect sympathomimetic activities (i.e., mixed-acting).  However, guanethidine intensified the mydriasis produced by phenylephrine, which is also thought to be mixed-acting.  Conversely, ephedrine has been shown to partially reverse the hypotensive effect of guanethidine.', 'DDInter', 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.  Alternatives to postganglionic adrenergic blocking agents should be considered if patients are treated with sympathomimetic amines, since effects of the latter may be intensified or diminished depending on whether they are direct- or indirect-acting.  Most agents with indirect sympathomimetic activity are mixed-acting, thus it may be difficult to predict how they will be affected by postganglionic adrenergic blocking agents.  If the combination is used, blood pressure and heart rate should be monitored.', 'Antagonism', 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300740/', '', 'Prazosin, Clonidine, Hydralazine, Guanfacine, Clonidine, Methyldopa', 'Dicyclomine, Mepenzolate, Alosetron, Glycopyrronium, Papaverine, Propantheline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253199, 'Isometheptene', 'Desvenlafaxine', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors.', 'DDInter', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.  Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter.  Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300741/', '', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Vortioxetine, Doxepin, Viloxazine, Amoxapine, Imipramine, Esketamine, More', 'Dicyclomine, Mepenzolate, Alosetron, Glycopyrronium, Papaverine, Propantheline', 1767369485);
