INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253496, 'Aripiprazole', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of aripiprazole and its active metabolite, dehydro-aripiprazole.', 'DDInter', 'When aripiprazole is administered orally, the prescribing information recommends doubling the usual dosage over 1 to 2 weeks following the addition of a potent CYP450 3A4 inducer.  Additional dosage adjustments should be based on clinical evaluation.  Upon discontinuation of the inducer, aripiprazole dosage should be reduced to the original level over 1 to 2 weeks.  When given as an immediate-acting intramuscular injection, the aripiprazole dosage should also be doubled in patients receiving potent CYP450 3A4 inducers.  For patients receiving extended-release intramuscular formulations of aripiprazole, concomitant use of potent CYP450 3A4 inducers should generally be avoided for greater than 14 days, although some formulations may be administered, with or without a dosage adjustment depending on the dose.  The prescribing information for individual aripiprazole products should be consulted for specific recommendations regarding concomitant use with potent CYP450 3A4 inducers.', 'Metabolism', 'When aripiprazole is administered orally, the prescribing information recommends doubling the usual dosage over 1 to 2 weeks following the addition of a potent CYP450 3A4 inducer.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301061/', '', 'Fluphenazine, Thiothixene, Amisulpride, Promazine, Prochlorperazine, Thioridazine, Pimozide, Trifluoperazine, Paliperidone, Chlorpromazine, Mesoridazine, More', 'Nitric Oxide, Doxapram, Elexacaftor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253497, 'Armodafinil', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme.', 'DDInter', 'Pharmacologic response to modafinil or armodafinil should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.  Dose adjustments may be required if an interaction is suspected.', 'Metabolism', 'Pharmacologic response to modafinil or armodafinil should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301062/', '', 'Amphetamine, Pemoline, Atomoxetine, Metamfetamine, Lisdexamfetamine, Solriamfetol, Caffeine, Methylphenidate, Dexmethylphenidate, Dextroamphetamine, Citicoline', 'Nitric Oxide, Doxapram, Elexacaftor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253498, 'Atazanavir', 'Lumacaftor', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.  Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended.  Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301064/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Telaprevir, Velpatasvir, Dolutegravir, Oseltamivir, More', 'Nitric Oxide, Doxapram, Elexacaftor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253499, 'Atorvastatin', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of atorvastatin and its active metabolites, all of which are substrates of the isoenzyme.', 'DDInter', 'The potential for diminished pharmacologic effects of atorvastatin should be considered during coadministration with CYP450 3A4 inducers.  Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible.  Otherwise, pharmacologic response to atorvastatin should be closely monitored, and the dosage adjusted as necessary.  A statin that is not metabolized by CYP450 3A4 such as fluvastatin, pitavastatin, pravastatin, or rosuvastatin may also be substituted for atorvastatin when used with certain enzyme inducers.', 'Metabolism', 'The potential for diminished pharmacologic effects of atorvastatin should be considered during coadministration with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301066/', '', 'Ramipril, Valsartan, Rosuvastatin, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, More', 'Nitric Oxide, Doxapram', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253500, 'Avapritinib', 'Lumacaftor', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9.', 'DDInter', 'Concomitant use of avapritinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301069/', '', 'Futibatinib, Lenvatinib, Trametinib, Afatinib, Dacomitinib, Pazopanib, Alectinib', 'Nitric Oxide, Doxapram, Elexacaftor', 1767369485);
