INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253596, 'Felbamate', 'Lumacaftor', 'Moderate', 'Coadministration with potent CYP450 3A4 inducers may decrease the plasma concentrations of felbamate, which may be a substrate of the isoenzyme.  Pharmacokinetic data may not be available for all potent inducers with respect to their effect on felbamate.  However, the potent CYP450 3A4 inducers carbamazepine, phenobarbital, and phenytoin have all been shown to decrease felbamate plasma concentrations.', 'DDInter', 'Caution is advised if felbamate is used concomitantly with potent CYP450 3A4 inducers.  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 inducer is added to or withdrawn from therapy with felbamate.', 'Metabolism', 'Caution is advised if felbamate is used concomitantly with potent CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301177/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', 'Nitric Oxide, Doxapram, Elexacaftor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253597, 'Felodipine', 'Lumacaftor', 'Major', 'Potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of calcium channel blockers, the majority of which are primarily metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers should generally be avoided.  If coadministration is necessary, pharmacologic response should be monitored more closely following the initiation or discontinuation of the CYP450 3A4 inducer, and the CCB dosage adjusted accordingly.', 'Metabolism', 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301178/', '', 'Propranolol, Metoprolol, Carvedilol, Nebivolol, Bisoprolol, Sotalol, Acetylsalicylic acid, Atenolol, Clevidipine, Mibefradil, Nisoldipine, More', 'Nitric Oxide, Doxapram, Elexacaftor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253598, 'Fentanyl', 'Lumacaftor', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of opioids that are primarily metabolized by the isoenzyme such as butorphanol, fentanyl, hydrocodone, and oxycodone.  Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of a CYP450 3A4 inducer.  Conversely, discontinuation of the inducer may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301179/', '', 'Halothane, Thiopental, Remifentanil, Sodium oxybate, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Sufentanil, Enflurane, Esketamine, More', 'Doxapram, Elexacaftor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253599, 'Fesoterodine', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme.', 'DDInter', 'No dosing adjustments are recommended when fesoterodine is used with CYP450 3A4 inducers.  However, the possibility of diminished therapeutic effects should be considered.', 'Metabolism', 'No dosing adjustments are recommended when fesoterodine is used with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301180/', '', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Tolterodine, Phenazopyridine, Trospium, Alprostadil, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, More', 'Nitric Oxide, Doxapram, Elexacaftor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253600, 'Fexofenadine', 'Lumacaftor', 'Moderate', 'Based on in vitro data demonstrating both inhibition and induction of P-glycoprotein (P-gp) efflux transporter, lumacaftor has the potential to alter the plasma concentrations of drugs that are substrates of P-gp.  Additionally, a clinical study with ivacaftor monotherapy showed that ivacaftor is a weak inhibitor of P-gp.  Therefore, concomitant use of lumacaftor/ivacaftor with P-gp substrates may alter the systemic exposure to these substrates.', 'DDInter', 'Caution is advised when lumacaftor is used concurrently with medications that are substrates of the P-glycoprotein efflux transporter, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumacaftor is added to or withdrawn from therapy.', 'Absorption', 'Caution is advised when lumacaftor is used concurrently with medications that are substrates of the P-glycoprotein efflux transporter, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301182/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Promethazine, More', 'Nitric Oxide, Doxapram', 1767369485);
